t.script:
onSubmit = function() {
    $('textarea[class="summernote"]').each(function() {
        $(this).html($(this).code());
    });
    document.getElementById('car_form').submit();
};
onSendToReview = function() {
    $('input[name=state]').attr('value', 'review');
    $('textarea[class="summernote"]').each(function() {
        $(this).html($(this).code());
    });
    document.getElementById('car_form').submit();
};
onOpenCar = function() {
    $('input[name=state]').attr('value', 'open');
    $('textarea[class="summernote"]').each(function() {
        $(this).html($(this).code());
    });
    document.getElementById('car_form').submit();
};

